package euphoria.common;import android.app.AlertDialog;import android.app.AlertDialog.Builder;import android.app.Dialog;import android.content.Context;import android.content.DialogInterface;import android.view.Window;import android.view.WindowManager.LayoutParams;import android.widget.EditText;public class Dialogs {    public static void showConfirmDialog(Context context,                                         String title,                                         String messsage,                                         DialogInterface.OnClickListener positiveListener) {        AlertDialog.Builder builder = new Builder(context);        builder.setTitle(title)                .setMessage(messsage);        builder.setPositiveButton(android.R.string.ok, positiveListener);        builder.setNegativeButton(android.R.string.cancel, (dialog, which) -> dialog.dismiss());        builder.show();    }    public static void showEditDialog(Context context,                                      Listener listener) {        EditText editText = new EditText(context);        if (listener != null)            listener.setupEditText(editText);        AlertDialog dlg = new AlertDialog.Builder(context)                .setView(editText)                .setPositiveButton(android.R.string.ok, (dialog, which) -> {                    if (listener != null) listener.onPositive(dialog, editText.getText());                }).create();        editText.requestFocus();        Window window = dlg.getWindow();        if (window != null)            window.setSoftInputMode(LayoutParams.SOFT_INPUT_STATE_VISIBLE);        dlg.show();    }    public interface Listener {        void setupEditText(EditText editText);        void onPositive(DialogInterface dialog, CharSequence text);    }}