package euphoria.common;import android.util.Log;import android.widget.Toast;import euphoria.psycho.knife.Contexts;public class Logger {    public static void t(String message) {        if (Contexts.getContext() != null)            Toast.makeText(Contexts.getContext(), message, Toast.LENGTH_LONG).show();    }    public static void t(Exception e) {        t(String.format("Message: %s\nCause: %s\n"                , e.getMessage()                , e.getCause()));    }    private static String getTag(Object obj) {        return "TAG/" + obj.getClass().getSimpleName();    }    public static void i(Object obj, String format, Object... args) {        Log.i(getTag(obj), format(format, args));    }    public static void d(Object obj, String format, Object... args) {        Log.d(getTag(obj), format(format, args));    }    public static void w(Object obj, String format, Object... args) {        Log.w(getTag(obj), format(format, args));    }    public static void e(Object obj, String msg, Throwable tr) {        Log.e(getTag(obj), msg, tr);    }    public static void e(Object obj, String format, Object... args) {        Log.e(getTag(obj), format(format, args));    }    private static String format(String format, Object... args) {        return args.length > 0 ? String.format(format, args) : format;    }}
