package euphoria.common;import android.app.Activity;import android.content.Context;import android.graphics.Point;import android.graphics.PointF;import android.util.DisplayMetrics;import android.util.Log;import android.view.View;import android.view.View.OnClickListener;import android.view.Window;import android.view.WindowManager;public class Views {    public static void onClicks(OnClickListener onClickListener, Activity parent, int... resIds) {        for (int i = 0, j = resIds.length; i < j; i++) {            View view = parent.findViewById(resIds[i]);            if (view != null) {                view.setOnClickListener(onClickListener);            }        }    }    public static int dp2px(Context context, int v) {        float density = context.getResources().getDisplayMetrics().density;        return (int) (v * density + 0.5f);    }    public static void dumpSize(Context context) {        DisplayMetrics metrics = new DisplayMetrics();        WindowManager manager = (WindowManager) context.getSystemService(Context.WINDOW_SERVICE);        manager.getDefaultDisplay().getMetrics(metrics);        Point realSize = new Point();        manager.getDefaultDisplay().getRealSize(realSize);        Point size = new Point();        manager.getDefaultDisplay().getSize(size);        Log.e("TAG/", "Debug: dumpSize, \n"                + " widthPixels = " + metrics.widthPixels + "\n"                + " heightPixels = " + metrics.heightPixels + "\n"                + " density = " + metrics.density + "\n"                + " densityDpi = " + metrics.densityDpi + "\n"                + " scaledDensity = " + metrics.scaledDensity + "\n"                + " xdpi = " + metrics.xdpi + "\n"                + " ydpi = " + metrics.ydpi + "\n"                + " getRealSize = " + realSize + "\n"                + " getSize = " + size + "\n"        );    }}