package euphoria.psycho.knife.helpers;import android.app.Activity;import android.content.Intent;import android.content.res.Resources;import android.graphics.drawable.Drawable;import android.util.SparseArray;import euphoria.common.Files.FileSort;import euphoria.psycho.common.C;import euphoria.psycho.knife.R;import euphoria.psycho.knife.util.ContextUtils;import euphoria.video.PlayerActivity;import euphoria.video.PlayerFragment;public class DocumentHelper {    /*打开视频文件*/    public static void launchVideo(Activity activity, String path, FileSort fileSort, boolean mIsAscending, int requestCode) {        Intent launch = new Intent(activity, PlayerActivity.class);        launch.putExtra(PlayerFragment.EXTRA_VIDEO_PATH, path);        launch.putExtra(PlayerFragment.EXTRA_SORT, fileSort.getValue());        launch.putExtra(PlayerFragment.EXTRA_DIRECTION, mIsAscending);        activity.startActivityForResult(launch, requestCode);    }}